/**
 * Based on: main.js from http://www.codrops.com
 */

var menu = {
    states: [ "open", "closed" ],
    button: {
        class: ".menu-button",
        attr: "data-state"
    },
    list: {
        class: ".menu-wrap",
        attr: "data-menu-state"
    },
    main: {
        class: ".main",
        attr: "data-menu-state"
    },
    toggle: function( el ) {
        var state = $( el ).attr( this.button.attr ),
            idx = ( this.states.indexOf( state ) === 0 ) ? 1 : 0;
        $( el ).attr( this.button.attr, this.states[ idx ] );
        $( this.main.class ).attr( this.main.attr, this.states[ idx ] );
        $( this.list.class ).attr( this.list.attr, this.states[ idx ] );
    }
};

$( menu.button.class ).on( "click", function() {
    menu.toggle( this );
} );
